    def search(list, string):
        list.clear()
        names=[]
        index = 0
        for x in range(len(Ui_EmployeePage.employee_id)):
            full_name = (f"{Ui_EmployeePage.employee_lname[index]}, {Ui_EmployeePage.employee_fname[index]} {Ui_EmployeePage.employee_mname[index]}.\n[{Ui_EmployeePage.employee_position[index]}]")
            item = QtWidgets.QListWidgetItem(full_name)
            item.setBackground(QColor('#666666')) if (index % 2) == 0 else item.setBackground(QColor('#595959'))
            item.setTextAlignment(QtCore.Qt.AlignTrailing | QtCore.Qt.AlignVCenter)
            item.value = Ui_EmployeePage.employee_id[index]
            list.addItem(item)
            names.append(full_name)
            index += 1

        if string != "":
            items = list.findItems(string, Qt.MatchStartsWith)
            if len(items) > 0:
                searched = {}
                z = 0
                for item in items:
                    list.takeItem(list.row(item))
                    searched[z] = item
                    z += 1

                list.clear()
                for i in range(len(searched)):
                    j = 0
                    while (searched[i].text() != names[j]):
                        j += 1
                    searched[i].value = Ui_EmployeePage.employee_id[j]
                    searched[i].setBackground(QColor('#666666')) if (i % 2) == 0 else searched[i].setBackground(QColor('#595959'))
                    searched[i].setTextAlignment(QtCore.Qt.AlignTrailing | QtCore.Qt.AlignVCenter)
                    list.addItem(searched[i])
            else:
                list.clear()



