    def submitForm(LELastName,LEFirstName,LEMI,LEAge,LEContact, EAddress,DEBirthday,
        DEDateHired,CbStatus,CbWorkStatus,CbJobPosition,LBImageContainer,LESuffix,CbSex,CbNationality):

        if (LELastName.text() == "") | (LEFirstName.text() =="")| (LEMI.text() =="") |(LEAge.text() =="") |(LEContact.text() =="") |(EAddress.text() ==""):
            msgBox = QMessageBox()
            msgBox.setIcon(QMessageBox.Information)
